import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import { Zap, Globe, Briefcase, Users } from 'lucide-react';
import ChatBot from '../components/ChatBot';
import './HomePage.css';

function HomePage() {
  const [agentCount, setAgentCount] = useState(0);
  const [websiteCount, setWebsiteCount] = useState(0);
  const [projectCount, setProjectCount] = useState(0);
  const [replaceCount, setReplaceCount] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setAgentCount(prev => (prev < 8 ? prev + 1 : 8));
    }, 100);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setWebsiteCount(prev => (prev < 40 ? prev + 1 : 40));
    }, 100);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setProjectCount(prev => (prev < 8 ? prev + 1 : 8));
    }, 100);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setReplaceCount(prev => (prev < 50 ? prev + 1 : 50));
    }, 100);
    return () => clearInterval(interval);
  }, []);

  {/* Update Teammates Replaced by AI Count */}
      const teammatesReplacedByAI = 15;
      useEffect(() => {
        const interval = setInterval(() => {
          setReplaceCount(prev => {
            if (prev < 10) {
              return prev + 1;
            } else {
              clearInterval(interval); // Ensure the interval stops when the count reaches the target
              return prev;
            }
          });
        }, 500); // Increased interval duration for smoother updates
        return () => clearInterval(interval);
      }, [teammatesReplacedByAI]);

  return (
    <div className="home-page">
      {/* Background Video */}
      <div className="video-background">
        <video
          autoPlay
          muted
          loop
          className="background-video"
          poster="/Bg_video/thumbnail.jpg"
        >
          <source src="/Bg_video/background.mp4" type="video/mp4" />
          {/* Fallback gradient if video doesn't load */}
        </video>
        <div className="video-overlay"></div>
      </div>

      {/* Hero Section */}
      <motion.section
        className="hero-section"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        <motion.div
          className="hero-content"
          initial={{ scale: 0.9, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          transition={{ delay: 0.2, duration: 0.6 }}
        >
          <h1 className="hero-title">KMN IT & Consulting</h1>
          <p
            className="hero-slogan focus-in-contract"
            style={{ animationDelay: '0s', animationDuration: '10s' }}
          >
            We make you online
          </p>
          <motion.p
            className="agent-replacement text-blur-out"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 2, duration: 10 }}
          >
            Replace teammates with AI agents
          </motion.p>
          <motion.button
            className="cta-button"
            whileHover={{ scale: 1.05, boxShadow: '0 10px 40px rgba(66, 165, 245, 0.4)' }}
            whileTap={{ scale: 0.95 }}
          >
            Get Started
          </motion.button>
        </motion.div>
      </motion.section>

      {/* Services Section */}
      <motion.section
        className="services-section"
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        transition={{ duration: 0.8 }}
        viewport={{ once: true }}
      >
        <h2 className="section-title">Our Services</h2>
        <div className="services-grid">
          {[
            {
              title: 'Website Development',
