import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import { Zap, Globe, Briefcase, Users } from 'lucide-react';
import ChatBot from '../components/ChatBot';
import './HomePage.css';

function HomePage() {
  const [agentCount, setAgentCount] = useState(0);
  const [websiteCount, setWebsiteCount] = useState(0);
  const [projectCount, setProjectCount] = useState(0);
  const [replaceCount, setReplaceCount] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setAgentCount(prev => (prev < 15 ? prev + 1 : 15));
    }, 100);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setWebsiteCount(prev => (prev < 40 ? prev + 1 : 40));
    }, 100);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setProjectCount(prev => (prev < 8 ? prev + 1 : 8));
    }, 100);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setReplaceCount(prev => (prev < 50 ? prev + 1 : 50));
    }, 100);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    const counterElement = document.getElementById('ai-agents-counter');
    let count = 0;
    const target = 150; // Replace with the actual number of teammates replaced
    const increment = Math.ceil(target / 100);

    const updateCounter = () => {
      if (count < target) {
        count += increment;
        counterElement.textContent = count;
        setTimeout(updateCounter, 50);
      } else {
        counterElement.textContent = target;
      }
    };

    updateCounter();
  }, []);

  return (
    <div className="home-page">
      {/* Background Video */}
      <div className="video-background">
        <video
          autoPlay
          muted
          loop
          className="background-video"
          poster="/Bg_video/thumbnail.jpg"
        >
          <source src="/Bg_video/background.mp4" type="video/mp4" />
          {/* Fallback gradient if video doesn't load */}
        </video>
        <div className="video-overlay"></div>
      </div>

      {/* Hero Section */}
      <motion.section
        className="hero-section"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        <motion.div
          className="hero-content"
          initial={{ scale: 0.9, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          transition={{ delay: 0.2, duration: 0.6 }}
        >
          <h1 className="hero-title">KMN IT & Consulting</h1>
          <p className="hero-slogan">We make you online</p>
          <motion.button
            className="cta-button"
            whileHover={{ scale: 1.05, boxShadow: '0 10px 40px rgba(66, 165, 245, 0.4)' }}
            whileTap={{ scale: 0.95 }}
          >
            Get Started
          </motion.button>
        </motion.div>
      </motion.section>

      {/* AI Agent Count */}
      <motion.section
        className="agent-section"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.5, duration: 0.8 }}
      >
        <div className="agent-card">
          <Zap className="agent-icon" size={40} />
          <div className="agent-count-display">
            <h2>Active AI Agents</h2>
            <motion.p
              className="agent-number"
              key={agentCount}
