import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import { Zap, Globe, Briefcase } from 'lucide-react';
import ChatBot from '../components/ChatBot';
import './HomePage.css';

function HomePage() {
  const [agentCount, setAgentCount] = useState(0);
  const [websiteCount, setWebsiteCount] = useState(0);
  const [projectCount, setProjectCount] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setAgentCount(prev => (prev < 15 ? prev + 1 : 15));
    }, 100);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setWebsiteCount(prev => (prev < 40 ? prev + 1 : 40));
    }, 100);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setProjectCount(prev => (prev < 8 ? prev + 1 : 8));
    }, 100);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="home-page">
      {/* Background Video */}
      <div className="video-background">
        <video
          autoPlay
          muted
          loop
          className="background-video"
          poster="/Bg_video/thumbnail.jpg"
        >
          <source src="/Bg_video/background.mp4" type="video/mp4" />
          {/* Fallback gradient if video doesn't load */}
        </video>
        <div className="video-overlay"></div>
      </div>

      {/* Hero Section */}
      <motion.section
        className="hero-section"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        <motion.div
          className="hero-content"
          initial={{ scale: 0.9, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          transition={{ delay: 0.2, duration: 0.6 }}
        >
          <h1 className="hero-title">KMN IT & Consulting</h1>
          <p className="hero-slogan">We make you online</p>
          <motion.button
            className="cta-button"
            whileHover={{ scale: 1.05, boxShadow: '0 10px 40px rgba(66, 165, 245, 0.4)' }}
            whileTap={{ scale: 0.95 }}
          >
            Get Started
          </motion.button>
        </motion.div>
      </motion.section>

      {/* AI Agent Count */}
      <motion.section
        className="agent-section"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.5, duration: 0.8 }}
      >
        <div className="agent-card">
          <Zap className="agent-icon" size={40} />
          <div className="agent-count-display">
            <h2>Active AI Agents</h2>
            <motion.p
              className="agent-number"
              key={agentCount}
              initial={{ scale: 1.5, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              transition={{ duration: 0.3 }}
            >
              {agentCount}
            </motion.p>
          </div>
        </div>

        <div className="agent-card">
          <Globe className="agent-icon" size={40} />
          <div className="agent-count-display">
            <h2>Completed Websites</h2>
            <motion.p
              className="agent-number"
              key={websiteCount}
              initial={{ scale: 1.5, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              transition={{ duration: 0.3 }}
            >
              {websiteCount}
            </motion.p>
          </div>
        </div>
      </motion.section>

      {/* Services Section */}
      <motion.section
        className="services-section"
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        transition={{ duration: 0.8 }}
        viewport={{ once: true }}
      >
        <h2 className="section-title">Our Services</h2>
        <div className="services-grid">
          {[
            {
              title: 'Website Development',
              description: 'Modern, responsive websites built with cutting-edge technologies',
              icon: 'ðŸŒ',
            },
            {
              title: 'AI Agents',
              description: 'Intelligent automation solutions powered by advanced AI',
              icon: 'ðŸ¤–',
            },
          ].map((service, index) => (
            <motion.div
              key={index}
              className="service-card"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.2, duration: 0.6 }}
              viewport={{ once: true }}
              whileHover={{
                y: -10,
                boxShadow: '0 20px 40px rgba(66, 165, 245, 0.3)',
              }}
            >
              <div className="service-icon">{service.icon}</div>
              <h3>{service.title}</h3>
              <p>{service.description}</p>
            </motion.div>
          ))}
        </div>
      </motion.section>

      {/* Leadership Preview Section */}
      <motion.section
        className="leadership-preview"
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        transition={{ duration: 0.8 }}
        viewport={{ once: true }}
      >
        <h2 className="section-title">Our Leadership</h2>
        <div className="leadership-grid">
          {[
            { role: 'CEO', name: 'Rajesh Kumar', bio: 'Visionary leader with 20+ years of experience' },
            { role: 'CTO', name: 'Priya Singh', bio: 'Tech innovator driving digital transformation' },
            { role: 'COO', name: 'Amit Patel', bio: 'Operations expert ensuring excellence' },
          ].map((leader, index) => (
            <motion.div
              key={index}
              className="leadership-card"
              initial={{ opacity: 0, scale: 0.9 }}
              whileInView={{ opacity: 1, scale: 1 }}
              transition={{ delay: index * 0.2, duration: 0.6 }}
              viewport={{ once: true }}
              whileHover={{
                scale: 1.05,
                boxShadow: '0 25px 50px rgba(66, 165, 245, 0.25)',
              }}
            >
              <div className="leader-avatar">{leader.name.charAt(0)}</div>
              <h3>{leader.name}</h3>
              <p className="role">{leader.role}</p>
              <p className="bio">{leader.bio}</p>
            </motion.div>
          ))}
        </div>
      </motion.section>

      {/* Contact Preview */}
      <motion.section
        className="contact-preview"
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        transition={{ duration: 0.8 }}
        viewport={{ once: true }}
      >
        <div className="contact-preview-content">
          <h2>Ready to Get Started?</h2>
          <p>Let's discuss how we can help your business grow online</p>
          <motion.button
            className="contact-cta-button"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            Contact Us
          </motion.button>
        </div>
      </motion.section>

      {/* Footer */}
      <footer className="footer">
        <div className="footer-content">
          <p>&copy; 2024 KMN IT & Consulting. All rights reserved.</p>
          <div className="footer-links">
            <a href="#privacy">Privacy Policy</a>
            <a href="#terms">Terms of Service</a>
            <a href="#contact">Contact</a>
          </div>
        </div>
      </footer>

      {/* ChatBot */}
      <ChatBot />
    </div>
  );
}

export default HomePage;
